import java.io.*;/** * <p> * Title: Scrivefile * </p> *  * <p> * Description: Una semplice application che scrive e rilegge un file * </p> *  * <p> * Copyright: Copyright (c) 2009 * </p> *  * <p> * Company: * </p> *  * @author Riccardo Cassinis * @version 1.0 */public class Scrivefile {  public static void main(String[] args) throws IOException {    System.out.println("Partito il metodo main di Scrivefile");    FileWriter out = new FileWriter("eccoilfile.dat");    for(int i = 0; i < 10; i ++) {      out.write("Buongiorno " + i);      out.write("\n");    }    System.out.println("closing...");      out.close();int inByte;  FileReader in = new FileReader("eccoilfile.dat");  do {    inByte=in.read();    if (inByte !=-1) System.out.print((char) inByte);  }  while (inByte != -1);    in.close();}}